import { connect } from 'react-redux'
import React, { createRef, Component } from 'react'
import L from 'leaflet'
import {
    Circle,
    CircleMarker,
    Map,
    Marker,
    Polygon,
    Popup,
    Rectangle,
    TileLayer,
    Tooltip,
} from 'react-leaflet'
const center = [4.620943145474099, -74.07446100531007]

const multiPolygon = [
    [[51.51, -0.12], [51.51, -0.13], [51.53, -0.13]],
    [[51.51, -0.05], [51.51, -0.07], [51.53, -0.07]],
]

const rectangle = [[51.49, -0.08], [51.5, -0.06]]

export class Mapa extends Component {
    constructor(props) {
        super(props)

        this.state = {
            hasLocation: false,
            latlng: {
                lat: 4.650010884129616,
                lng: -74.10284221172334,
            },
            clicked: 0,
            draggable: true,
            pointStack:[{
                lat: 4.650010884129616,
                lng: -74.10284221172334,
            }]
        }
        this.handleClick = this.handleClick.bind(this);

        this.handleLocationFound = this.handleLocationFound.bind(this);
        this.toggleDraggable = this.toggleDraggable.bind(this);
        this.updatePosition = this.updatePosition.bind(this);

    }
    onClickCircle = () => {
        this.setState({ clicked: this.state.clicked + 1 })
    }

    handleClick = (e) => {
        console.log(e);
        this.setState({ pointStack: e.latlng })
        // const map = this.mapRef.current
        // if (map != null) {
        //     map.leafletElement.locate()
        // }
    }

    handleLocationFound(e) {

        console.log(e + "handleLocationFound")
        this.setState({
            hasLocation: true,
            latlng: e.latlng,
        })
    }
    componentDidMount() {
        console.log(this)
    }
    toggleDraggable = () => {
        this.setState({ draggable: !this.state.draggable })
      }

      updatePosition = () => {
        // const marker = this.refmarker.current
        // if (marker != null) {
        //   this.setState({
        //     marker: marker.leafletElement.getLatLng(),
        //   })
        // }
      }
    render() {

        const clickedText =
            this.state.clicked === 0
                ? 'Click this Circle to change the Tooltip text'
                : `Circle click: ${this.state.clicked}`
        const marker = this.state.hasLocation ? (
            <Marker position={this.state.latlng}>
                <Popup>You are here</Popup>
            </Marker>
        ) : null
        const marketList = this.props.coordenates.list.map((marker, key) => {
            return (
                <Marker position={marker}>
                    <Popup>You are ajsjahse</Popup>
                </Marker>
            )
        })
        const marketListNew = this.state.pointStack.map((marker, key) => {
