import { connect } from 'react-redux'
import React, { createRef, Component } from 'react'
import {
    Circle,
    CircleMarker,
    Map,
    Marker,
    Polygon,
    Popup,
    Rectangle,
    TileLayer,
    Tooltip,
} from 'react-leaflet'
const center = [51.505, -0.09]

const multiPolygon = [
    [[51.51, -0.12], [51.51, -0.13], [51.53, -0.13]],
    [[51.51, -0.05], [51.51, -0.07], [51.53, -0.07]],
]

const rectangle = [[51.49, -0.08], [51.5, -0.06]]

export class Mapa extends Component {
    constructor(props) {
        super(props)

        this.state = {
            hasLocation: false,
            latlng: {
                lat: 51.505,
                lng: -0.09,
            },
            clicked: 0,
        }
        this.handleClick = this.handleClick.bind(this);

        this.handleLocationFound = this.handleLocationFound.bind(this);
        this.onClickCircle = this.onClickCircle.bind(this);

    }

    onClickCircle = () => {
        this.setState({ clicked: this.state.clicked + 1 })
    }

    handleClick = () => {
        const map = this.mapRef.current
        if (map != null) {
            map.leafletElement.locate()
        }
    }

    handleLocationFound(e) {
        this.setState({
            hasLocation: true,
            latlng: e.latlng,
        })
    }
    componentDidMount() {
        console.log(this)
    }
    render() {
        const clickedText =
            this.state.clicked === 0
                ? 'Click this Circle to change the Tooltip text'
                : `Circle click: ${this.state.clicked}`
        const marker = this.state.hasLocation ? (
            <Marker position={this.state.latlng}>
                <Popup>You are here</Popup>
            </Marker>
        ) : null
        const marketList = this.props.coordenates.list.map(marker => {
            // console.log(marker)
            return (
                <Marker position={marker}>
                    <Popup>You are ajsjahse</Popup>
                </Marker>
            )
        })
        return (
            <div >
                <Map center={center} zoom={15}>
                    <TileLayer
                        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Circle
                        center={center}
                        fillColor="blue"
                        onClick={this.onClickCircle}
                        radius={200}>
                        <Tooltip>{clickedText}</Tooltip>
                    </Circle>
                    <CircleMarker center={[51.51, -0.12]} color="red" radius={20}>
                        <Tooltip>Tooltip for CircleMarker</Tooltip>
                    </CircleMarker>
                    <Marker position={[51.51, -0.09]}>
                        <Popup>Popup for Marker</Popup>
                        <Tooltip>Tooltip for Marker</Tooltip>
                    </Marker>
                    {marketList}
                    <Polygon color="purple" positions={multiPolygon}>
                        <Tooltip sticky>sticky Tooltip for Polygon</Tooltip>
                    </Polygon>
                    <Rectangle bounds={rectangle} color="black">
                        <Tooltip direction="bottom" offset={[0, 20]} opacity={1} permanent>
                            permanent Tooltip for Rectangle
                        </Tooltip>
                    </Rectangle>
                </Map>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    coordenates: state.coordenates
})

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Mapa)
